<template>    <div class="editCon">        <div class="panel-con">            <el-form :model="PassForm" ref="PassForm" :rules="rules">                <el-form-item label="原密码" prop="oldpass">                    <el-input v-model="PassForm.oldpass" show-password clearable autofocus></el-input>                </el-form-item>                <el-form-item label="新密码" prop="newpass">                    <el-input v-model="PassForm.newpass" aria-placeholder="" show-password clearable></el-input>                </el-form-item>                <el-form-item label="确认密码" prop="newpass2">                    <el-input v-model="PassForm.newpass2" show-password clearable></el-input>                </el-form-item>                <div class="btn-con">                    <el-button type="primary" @click="editPass">确定</el-button>                </div>            </el-form>        </div>    </div></template><script>    import {SUCCESS, URL} from "../../lib/base";    export default {        name: "EditPass",        data() {            return {                PassForm: {                    oldpass: '',                    newpass: '',                    newpass2: ''                },                rules: {                    oldpass: [                        {required: true, message: '请输入原始密码', trigger: "blur"}                    ],                    newpass: [                        {required: true, message: '请输入新密码', trigger: "blur"},                        {min: 6, message: '长度至少五个字符', trigger: 'blur'}                    ],                    newpass2: [                        {required: true, message: '请再次输入新密码', trigger: "blur"},                        {min: 6, message: '长度至少五个字符', trigger: 'blur'}                    ]                }            }        },        methods: {            editPass() {                this.$refs.PassForm.validate(result => {                    if (result) {                        let url = URL + '/admin/Login/editPass';                        let token = sessionStorage.getItem('token');                        this.$http({                            method: 'POST',                            url,                            data: this.PassForm,                            headers: {                                token                            }                        }).then(res => {                            if (res.status == 200 && res.data.code == SUCCESS) {                                this.$message.success(res.data.msg);                                sessionStorage.removeItem('token');                                this.$router.push({name: 'login'});                            } else {                                this.$message.error(res.data.msg);                            }                        }).catch(error => {                            console.log(error);                        })                    }                })            }        }    }</script><style scoped>    .panel-con {        width: 500px;        height: 400px;        background-color: white;        border-radius: 10px;        position: absolute;        top: 20%;        left: 50%;        transform: translateX(-50%);        padding: 20px;        box-sizing: border-box;    }    .btn-con {        width: 100%;        text-align: center;    }</style>